import React, { useEffect, useState } from "react";
import { Box, Card, CardContent, CardMedia, Grid } from "@mui/material";

function Home() {
  const [movies, setMovies] = useState([]);

  const getMovie = async () => { 
    try {
      const response = await fetch('https://api.themoviedb.org/3/discover/movie?api_key=824cd7fe0126902395b9fe320180c886'); // Fixed endpoint
      const json = await response.json();
      setMovies(json.results); 
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getMovie();
  }, []);

  return (
    <div>
      <Grid container spacing={2}>
        {movies.map((movie) => ( // Removed extra 'return'
          <Grid item xs={12} sm={6} md={3} key={movie.id}> {/* Adjust Grid item props as needed */}
            <Box>
              <Card>
                <CardContent>
                  <CardMedia
                    component="img"
                    height="140"
                    image={`https://image.tmdb.org/t/p/w500${movie.poster_path}`}
                  />
                </CardContent>
              </Card>
            </Box>
          </Grid>
        ))}
      </Grid>
    </div>
  );
}

export { Home };
