import React from "react";
import { useState, useEffect } from "react";
import { Box, Card, CardContent, CardMedia, Grid, Button } from "@mui/material";
import netflix from "../images/netflix.png";

function Navbar() {
    const [movies, setMovies] = useState([]);

  const getMovie =  () => {
    try {
       fetch(
        "https://api.themoviedb.org/3/discover/movie?api_key=824cd7fe0126902395b9fe320180c886") // Fixed endpoint
        .then((response) => response.json())
        .then((json) => setMovies(json.results));
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getMovie();
  }, []);

  console.log(movies[0])

    return (
        <div style={{backgroundImage: `url(https://image.tmdb.org/t/p/original${movies[8]?.poster_path})`, backgroundPosition: 'center', backgroundRepeat: 'no-repeat', backgroundSize: 'cover', height: '600px', width: '100%'}}>
            <div style={{display: 'flex', justifyContent: 'space-between', padding: '20px'}}>
            <img  style ={{width: '140px'}} src={netflix}/>
           <Button color= 'error' variant="contained" sx={{height: '40px'}}>SignIn</Button>
                </div> 
                <div style={{padding: '20px', color: '#f1f1f1'}}>
                  <h1 style={{color: '#f1f1f1', fontSize: '70px', fontFamily:`initial`}}>{movies[8]?.original_title}</h1>
                  <h3 style={{color: '#f1f1f1'}}>{movies[8]?.overview}</h3>
                </div>
                <Button variant="contained" sx={{color: "black", bgcolor: "white"}}>Watch Now</Button>

        </div>
    )
}

export {Navbar}
