import React from "react";
import { useState, useEffect } from "react";
import netflix from "../images/netflix.png";

function Navbar() {
    const [movies, setMovies] = useState([]);

  const getMovie =  () => {
    try {
       fetch(
        "https://api.themoviedb.org/3/discover/movie?api_key=824cd7fe0126902395b9fe320180c886") // Fixed endpoint
        .then((response) => response.json())
        .then((json) => setMovies(json.results));
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getMovie();
  }, []);

  console.log(movies[0])

    return (
        <div style={{backgroundImage: `url(https://image.tmdb.org/t/p/original${movies[8]?.poster_path})`, backgroundPosition: 'center', backgroundRepeat: 'no-repeat', backgroundSize: 'cover', height: '400px', width: '100%'}}>
           <img style={{width: '70px', height: '70px'}} src={netflix}/>
        </div>
    )
}

export {Navbar}
