import React, { useEffect, useState } from "react";
import { Box, Card, CardContent, CardMedia } from "@mui/material";

function Home() {
  const [movies, setMovies] = useState([]);

  const getMovie = async () => { // Note: Made getMovie async
    try {
      const response = await fetch('https://api.themoviedb.org/3/discover/movie?api_key=824cd7fe0126902395b9fe320180c886'); // Fixed endpoint
      const json = await response.json();
      setMovies(json.results); // Update state after fetching
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getMovie();
  }, []);

  // Inside your return
  return (
    <div>
      {movies.map((movie) => { 
        return (
          <Box>
            <Card>
              <CardContent>
                <CardMedia
                  component="img"
                  height="140"
                  image={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} 
                />
              </CardContent>
            </Card>
          </Box>
        );
      })}
    </div>
  );
}

export { Home }; // Export correctly
